import { useEffect, useState } from "react";
import "./photoGallery.css";
import { getStorage, ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import app from "../../firebase"
import { useDispatch, useSelector } from "react-redux";
import { addDesigns, addPhotoToGallery, get, getPhotoGalleries } from '../../redux/apiCalls'
import { addPhotoToGalleryStart } from "../../redux/photoGalleryRedux";



export default function PhotoGallery() {

  const [inputs, setInputs] = useState({})
  const [file, setFile] = useState(null)
  const [cat, setCat] = useState([])
  const [color, setColor] = useState([])
  const [size, setSize] = useState([])

  const allGalleries = useSelector(state => state.PhotoGallery)

  const [gallery, setGallery] = useState({
    galTitle: '',
    galDescription: '',
    photos: []
  })
  const [galTitlePre, setGalTitlePre] = useState('')
  const [filePre, setFilePre] = useState('')
  const [photo, setPhoto] = useState({
    phoTitle: "",
    phoDescription: "",
    source: "",
    createdDated: ''
  })
  const [photoPre, setPhotoPre] = useState('')

  const dispatch = useDispatch()

  const handleChange = (e) => {
    setInputs(prev => {
      return { ...prev, [e.target.name]: e.target.value }
    })
  }

  const handleGalleryTitle = (e) => {
    let gal = gallery
    gal.galTitle = e.target.value
    setGalTitlePre(e.target.value)
    setGallery(gal)
  }
  const handleGalleryDesc = (e) => {
    let gal = gallery
    gal.galDescription = e.target.value
    setGalTitlePre(e.target.value)
    setGallery(gal)
  }

  const handlePhotoTitle = (e) => {
    let pho = photo
    pho.phoTitle = e.target.value
    setGalTitlePre(e.target.value)
    setPhoto(pho)
  }
  const handlePhotoDesc = (e) => {
    let pho = photo
    pho.phoDescription = e.target.value
    setGalTitlePre(e.target.value)
    setPhoto(pho)
  }

  const handlePhoto = (e) => {
    console.log(e.target.files);
    let mainPre = ''
    if (e.target.value != null) {
      mainPre = URL.createObjectURL(e.target.files[0])
    }

    // console.log(newMain);
    // console.log(mainPre);
    setGalTitlePre(e.target.value)
    setFilePre(mainPre)
  }

  const handleClick = (e) => {
    e.preventDefault()
    console.log("Clicked");
    console.log(photo);
    console.log(gallery);
    console.log(file);

    let mainPho = ''
    let isSuccesful = false
    console.log(photo.source);
    let fil = file
    const fileName = fil?.name;
    const storage = getStorage(app)
    const StorageRef = ref(storage, `images/gallery/${gallery.galTitle}/${fileName}`)
    const uploadTask = uploadBytesResumable(StorageRef, fil);
    uploadTask.on('state_changed', (snapshot) => {
      const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
      console.log('Upload is ' + progress + '% done');
      switch (snapshot.state) {
        case 'paused':
          console.log('Upload is paused');
          break;
        case 'running':
          console.log('Upload is running');
          break;
        default:
      }
      console.log('Upload is ' + progress + '% done');
    },
      (error) => {
        // Handle unsuccessful uploads
        console.log(error);
      },
      async () => {
        await getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
          console.log('File available at', downloadURL);
          mainPho = downloadURL
          isSuccesful = true
        });

        if (isSuccesful) {
          let now = new Date();
          let month = (now.getMonth() + 1);
          let day = now.getDate();
          if (month < 10)
            month = "0" + month;
          if (day < 10)
            day = "0" + day;
          let today = now.getFullYear() + '-' + month + '-' + day;

          const pho = photo
          pho.source = mainPho
          pho.createdDated = today
          const galPhos = gallery.photos
          galPhos.push(pho)
          const newGal = { ...gallery, photos: galPhos }
          console.log(newGal);
          await addPhotoToGallery(newGal, dispatch).then((res) => {
            console.log("Success, we need to REFRSH");
          })
        }
      });

  }

  useEffect(() => {

    const getGalleries = () => {      
       getPhotoGalleries(dispatch)    
    }

    getGalleries()
  }, [dispatch]);



  console.log(allGalleries);



  return (
    <>

      <div className="main">
        <h1 className="addProductTitle">ADD GALLERY</h1>
        <div className="newProduct">
          <form className="addProductForm">
            <div className="addProductItem">
              <div className="addProductItem">
                <label>Gallery Name</label>
                <input name='title' type="text" placeholder="Apple Airpods" onChange={handleGalleryTitle} />
              </div>
              <div className="addProductItem">
                <label>Gallery Description</label>
                <input name='desc' type="text" name='description' placeholder="Description" onChange={handleGalleryDesc} />
              </div>
              <h1 className="addProductTitle">Photo Details</h1>

              <label>Image</label>
              <input type="file" name='source' onChange={handlePhoto} />
            </div>
            <div className="addProductItem">
              <label>Photo Title</label>
              <input name='title' type="text" placeholder="Apple Airpods" onChange={handlePhotoTitle} />
            </div>

            <div className="addProductItem">
              <label>Photo Description</label>

              <input name='desc' type="text" name='description' placeholder="Description" onChange={handlePhotoDesc} />
            </div>
            <div className="addProductItem">
              <label>Date</label>
              <input type="text" name='createdDate' onChange={handleChange} />
            </div>

            <button onClick={handleClick} className="addProductButton">Create</button>
          </form>
          <img src={filePre} />
          <h2>Gallery Title: {gallery.galTitle}</h2><br />
          <h3>Gallery Description: <br />{gallery.galDescription}</h3>
          <br /><br />
          <h2>Photo Title: {photo.phoTitle}</h2><br />
          <h3>Photo Description: <br />{photo.phoDescription}</h3>

        </div>

      </div>

    </>
  );
}
