import { useEffect, useState } from "react";
import "./newProduct.css";
import { getStorage, ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import app from "../../firebase"
import { useDispatch } from "react-redux";
import {addProducts} from '../../redux/apiCalls'



export default function NewProduct() {
  const [inputs, setInputs] = useState({})
  const [file, setFile] = useState(null)
  const [cat, setCat] = useState([])
  const [color, setColor] = useState(["white", "black", "red", "yellow"] )
  const [size, setSize] = useState(["S", "M", "L", "XL"])
  const dispatch = useDispatch()

  const handleChange =(e) => {
    setInputs(prev=>{
      return {...prev, [e.target.name]: e.target.value}
    })
  }
  const handleCat = (e) => {
    console.log(e.target.checked);

    let updatedCat= []
    cat?.map((c, index) => {

      if(c ===  e.target.value && !e.target.checked){
        updatedCat = cat.splice(index, 1)
        setCat(updatedCat)
      } else{
        setCat(cat =>  [...cat, e.target.value])
      }
    })

    console.log(cat);
  }

  const handleColor = (e) => {
    setColor(e.target.value.split(','))
  }
  const handleSize = (e) => {
    setSize(e.target.value.split(','))
  }

  const handleClick = (e) => {
    e.preventDefault()
    const fileName = new Date().getTime() + file.name;
    const storage = getStorage(app)
    const StorageRef = ref(storage, fileName)

  const uploadTask = uploadBytesResumable(StorageRef, file);

// Register three observers:
// 1. 'state_changed' observer, called any time the state changes
// 2. Error observer, called on failure
// 3. Completion observer, called on successful completion
uploadTask.on('state_changed', 
  (snapshot) => {
    // Observe state change events such as progress, pause, and resume
    // Get task progress, including the number of bytes uploaded and the total number of bytes to be uploaded
    const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
    console.log('Upload is ' + progress + '% done');
    switch (snapshot.state) {
      case 'paused':
        console.log('Upload is paused');
        break;
      case 'running':
        console.log('Upload is running');
        break;
        default:
    }
  }, 
  (error) => {
    // Handle unsuccessful uploads
  }, 
  () => {
    // Handle successful uploads on complete
    // For instance, get the download URL: https://firebasestorage.googleapis.com/...
    getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
      // console.log('File available at', downloadURL);
      const product = {...inputs, img: downloadURL, categories: cat, color, size };
      console.log(product);
      addProducts(product, dispatch)

    });
  }
);
}

useEffect(() => {

}, [cat]);


  return (
    <div className="newProduct">
      <h1 className="addProductTitle">New Product</h1>
      <form className="addProductForm">
        <div className="addProductItem">
          <label>Image</label>
          <input type="file" id="file" onChange={e=>setFile(e.target.files[0])}/>
        </div>
        <div className="addProductItem">
          <label>Title</label>
          <input name='title' type="text" placeholder="Apple Airpods" onChange={handleChange} />
        </div>
        <div className="addProductItem">
          <label>Price</label>
          <input name='price' type="number" placeholder="Price" onChange={handleChange} />
        </div>
        <div className="addProductItem">
          <label>Description</label>
          <input name='desc' type="text" placeholder="Description" onChange={handleChange} />
        </div>
        {/* <div className="addProductItem">
          <label>Category</label>
          <input name='categories' type="text" placeholder="shirt, kids, men, women" onChange={handleCat} />
        </div> */}
        <div className="addProductItem">
          <label>Category</label>
          <input  type="text" placeholder="shirt, kids, men, women"  />

            <ul className="checkbox-grid" name='categories' onChange={handleCat}>
              <li><input type="checkbox" name="text1" value="value1" /><label htmlFor="text1">Text 1</label></li>
              <li><input type="checkbox" name="text2" value="value2" /><label htmlFor="text2">Text 2</label></li>
              <li><input type="checkbox" name="text3" value="value3" /><label htmlFor="text3">Text 3</label></li>
              <li><input type="checkbox" name="text4" value="value4" /><label htmlFor="text4">Text 4</label></li>
              <li><input type="checkbox" name="text5" value="value5" /><label htmlFor="text5">Text 5</label></li>
              <li><input type="checkbox" name="text6" value="value6" /><label htmlFor="text6">Text 6</label></li>
              <li><input type="checkbox" name="text7" value="value7" /><label htmlFor="text7">Text 7</label></li>
              <li><input type="checkbox" name="text8" value="value8" /><label htmlFor="text8">Text 8</label></li>
          </ul>
        </div>
        <div className="addProductItem">
          <label>Color</label>
          <input name='color' type="text" placeholder="white, black, red, yellow" onChange={handleColor} />
        </div>
        <div className="addProductItem">
          <label>Size</label>
          <input name='categories' type="text" placeholder="S, M, L, XL" onChange={handleSize} />
        </div>
        <div className="addProductItem">
          <label>Stock</label>
          <select name='inStock' onChange={handleChange} >
            <option value='true'>Yes</option>
            <option value='false'>No</option>
          </select>
        </div>

        <button onClick={handleClick} className="addProductButton">Create</button>
      </form>
    </div>
  );
}
