import { useEffect, useState } from "react";
import "./photoGallery.css";
import { getStorage, ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import app from "../../firebase"
import { useDispatch } from "react-redux";
import {addDesigns, get, getPhotoGalleries} from '../../redux/apiCalls'



export default function PhotoGallery() {
  const [inputs, setInputs] = useState({})
  const [file, setFile] = useState(null)
  const [cat, setCat] = useState([])
  const [color, setColor] = useState([] )
  const [size, setSize] = useState([])
  const dispatch = useDispatch()

  const handleChange =(e) => {
    setInputs(prev=>{
      return {...prev, [e.target.name]: e.target.value}
    })
  }


  const handleCat = (e) => {        
    if(!cat.includes(e.target.value)){
      setCat(cat =>  [...cat, e.target.value])
    }else if(cat.includes(e.target.value)){
      cat.map((c, index) => {
        console.log('here');
        if(c === e.target.value && !e.target.checked){
          console.log('found');
          cat.splice(index, 1)          
          setCat(cat)     
       }
      })
    }
    console.log(cat);
  }

  const handleColor = (e) => {
    if(!color.includes(e.target.value)){
      setColor(color =>  [...color, e.target.value])
    }else if(color.includes(e.target.value)){
      color.map((c, index) => {
        console.log('here');
        if(c === e.target.value && !e.target.checked){
          console.log('found');
          color.splice(index, 1)          
          setColor(color)     
       }
      })
    }
    console.log(color);
  }

  const handleSize = (e) => {
    if(!size.includes(e.target.value)){
      setSize(size =>  [...size, e.target.value])
    }else if(size.includes(e.target.value)){
      size.map((c, index) => {
        console.log('here');
        if(c === e.target.value && !e.target.checked){
          console.log('found');
          size.splice(index, 1)          
          setSize(size)     
       }
      })
    }
    console.log(size);
  }
  // const handleColor = (e) => {
  //   setColor(e.target.value.split(','))
  // }
  // const handleSize = (e) => {
  //   setSize(e.target.value.split(','))
  // }

  const handleClick = (e) => {
    e.preventDefault()
    const fileName = new Date().getTime() + file.name;
