    //     console.log("bout tot call update");

    //     // setInputs(product => [...product, inputs])
    //     console.log(extraPhotos);
    //     console.log("running UPDATE");
    //     // handleUpdate()

    //   }
    }
    );
    console.log(pho);
    return pho
}
