import { createSlice } from '@reduxjs/toolkit';



export const photoGallerySlice = createSlice({
  name: 'photo',
  initialState:{
      photos: [],
      isFetching: false,
      error:false,
  },
  reducers: {
      //get all
    getPhotoGalleryStart: (state) => {
      state.isFetching = true 
      state.error = false
    },
    getPhotoGallerySuccess: (state, action) => {
      state.isFetching = false
      state.photos = action.payload
    },
    getPhotoGalleryFailure: (state) => {
      state.isFetching = false
      state.error = true
    },

      //get delete
    deletePhotoGalleryStart: (state) => {
      state.isFetching = true 
      state.error = false
    },
    deletePhotoGallerySuccess: (state, action) => {
      state.isFetching = false
      state.photos.splice(
          state.photos.findIndex((item) => item._id === action.payload),
          1
      )
    },
    deletePhotoGalleryFailure: (state) => {
      state.isFetching = false
      state.error = true
    },

// update PhotoGallery
    updatePhotoGalleryStart: (state) => {
      state.isFetching = true 
      state.error = false
    },
    updatePhotoGallerySuccess: (state, action) => {
      state.isFetching = false
      state.photos[
        state.photos.findIndex((item) => item._id === action.payload.id)
      ] = action.payload.photo;
    },

    updatePhotoGalleryFailure: (state) => {
      state.isFetching = false
      state.error = true
    },

// Add PhotoGallery
    addPhotoGalleryStart: (state) => {
      state.isFetching = true 
      state.error = false
    },
    addPhotoGallerySuccess: (state, action) => {
      state.isFetching = false
      state.photos.push(action.payload)
    },

    addPhotoGalleryFailure: (state) => {
      state.isFetching = false
      state.error = true
    },
  },
});

export const { getPhotoGalleryStart, getPhotoGallerySuccess, getPhotoGalleryFailure, 
  deletePhotoGalleryStart, deletePhotoGallerySuccess, deletePhotoGalleryFailure,
updatePhotoGallerySuccess, updatePhotoGalleryStart,updatePhotoGalleryFailure, addPhotoGallerySuccess, addPhotoGalleryStart, addPhotoGalleryFailure} = photoGallerySlice.actions;

export default photoGallerySlice.reducer;
