    let pho = []
    if(photos[idx] == undefined){
      pho = photo
    }else{
      pho= photos[idx]
    }
    pho.phoDescription = e.target.value
    setGalTitlePre(e.target.value)
    setPhoto(pho)
    const newPhotos = [...photos]
    newPhotos[idx] = pho
    setPhotos(newPhotos)
  }
  const handlePhoto = (e) => {
    console.log(e.target.files);
    let mainPre = []
    if (e.target.value != null) {
      let mP = []
      mP = Object.values(e.target.files)
      mP.map(file => {
        mainPre.push(URL.createObjectURL(file))
      })
      console.log(mainPre);
      setFiles(e.target.files)
      setGalTitlePre(e.target.value)
      setFilesPre(mainPre)
    } else {
      return
    }
  }
  const addtoGallery = (gal) => {
    console.log("here");
    console.log(gal);
    setIsUpdate(true)
    setShowModal(prev => !prev)
    setGallery(gal)
  }

  const handleClick = (e) => {
    e.preventDefault()
    setShowModal(prev => !prev)
    let mainPho = ''
    let isSuccesful = false
    console.log(photos);
    console.log(files);

    let fil = files
    const fileName = fil?.name;
    const storage = getStorage(app)
    const StorageRef = ref(storage, `images/gallery/${gallery.galTitle}/${fileName}`)


    // const uploadTask = uploadBytesResumable(StorageRef, fil);
