import { useDispatch, useSelector } from "react-redux";
import { getStorage, ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import app from "../firebase"
import { addPhotos } from "./productRedux";
import { updateProducts } from "./apiCalls";

export default function AddPhotoToFireBase(files1, product, dispatch) {

    let pho = []
    let files = new Array(files1)


    product?.morePhotos.map((photo, idx) => {
        if (files[0][idx] == null) {
                pho[idx] = photo
            files[0][idx] = photo
        } else if (files[0][idx] != null) {
                            pho[idx] = files[0][idx]
        }
    }
    )


    let itemsProcessed = 0
    let itemsProcessed1 = 0
   iles = false
    files[0].map((file, idx) => {


           if (typeof file == "object") {
            haveFiles = true

                               const fileName = product._id + file?.name;

            const storage = getStorage(app)
            const StorageRef = ref(storage, fileName)

            const uploadTask = uploadBytesResumable(StorageRef, file);
            // Register three observers:
            // 1. 'state_changed' observer, called any time the state changes
            // 2. Error observer, called on failure
            // 3. Completion observer, called on successful completion
            uploadTask.on('state_changed',
                (snapshot) => {
                    // Observe state change events such as progress, pause, and resume
                    // Get task progress, including the number of bytes uploaded and the total number of bytes to be uploaded
                    const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
                    console.log('Upload is ' + progress + '% done');
                    switch (snapshot.state) {
                        case 'paused':
                            console.log('Upload is paused');
                            break;
                        case 'running':
                            console.log('Upload is running');
                            break;
                        default:
                    }
                    console.log('Upload is ' + progress + '% done');
                },
                (error) => {
                    // Handle unsuccessful uploads
                    console.log(error);
                },
                async () => {
                    // Handle successful uploads on complete
                    // For instance, get the download URL: https://firebasestorage.googleapis.com/...
                    await getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
                        console.log('File available at', downloadURL);
                        // pho.push(downloadURL)
                        pho[idx] = downloadURL
                        itemsProcessed++
                        let pro = {};
                        if (itemsProcessed >= files.length) {
                            pro = { ...product, morePhotos: pho }
                            console.log(pro);
                            updateProducts(pro._id, pro, dispatch)
                            return pro;
                        }

                    });
                });

        }

    })
    if (!haveFiles) {
        console.log("We need to REFRSH");
    }
    console.log(pho);

}
