import { createSlice  } from "@reduxjs/toolkit";

const userSlice = createSlice({
    name: "user",
    initialState: {
       currentUser: null,
       isFetching: false,
       error: false
    },
    reducers:{
        loginStart:(state)=>{
            state.isFetching=true;
        },
        loginSuccess:(state, action)=>{
            state.isFetching=false;
            state.currentUser=action.payload;
        },
        loginFailure:(state)=>{
            state.isFetching=false;
            state.error = true;
        },    

        logOut: (state) => {
            state.currentUser = null
        },
     //get all
     getUsersStart: (state) => {
        state.isFetching = true 
        state.error = false
      },
      getUsersSuccess: (state, action) => {
        state.isFetching = false
        state.products = action.payload
      },
      getUsersFailure: (state) => {
        state.isFetching = false
        state.error = true
      },
        //get delete
      deleteUserStart: (state) => {
        state.isFetching = true 
        state.error = false
      },
      deleteUserSuccess: (state, action) => {
        state.isFetching = false
        state.users.splice(
            state.users.findIndex((item) => item._id === action.payload),
            1
        )
      },
      deleteUserFailure: (state) => {
        state.isFetching = false
        state.error = true
      },

  // update product
      updateUserStart: (state) => {
        state.isFetching = true 
        state.error = false
      },
      updateUserSuccess: (state, action) => {
        state.isFetching = false
        state.products[
          state.products.findIndex((item) => item._id === action.payload.id)
        ] = action.payload.product;
      },

      updateUserFailure: (state) => {
        state.isFetching = false
        state.error = true
      },


    },
  });
export const { loginStart, loginSuccess, loginFailure , logOut, getUsersSuccess,getUsersStart,getUsersFailure,
deleteUserSuccess,deleteUserStart, deleteUserFailure, updateUserStart, updateUserSuccess, updateUserFailure} = userSlice.actions;
export default userSlice.reducer;
